import { prisma } from "@/utils/prisma";
import { z } from "zod";
import { FastifyPluginAsyncTypeProvider } from "@/utils/fastify";
import {
  serializerCompiler,
  validatorCompiler,
} from "fastify-type-provider-zod";
import { validateRequestPassword } from "@/utils/password";
import { changePassword } from "@/utils/user";
import { validateAuthUser } from "@/utils/authCheck";
import bcrypt from "bcrypt";

const updateUserPasswordSchema = z.object({
  password: z.string(),
  new_password: z.string(),
  new_password_confirmation: z.string(),
});

const index: FastifyPluginAsyncTypeProvider = async (fastify) => {
  fastify.setSerializerCompiler(serializerCompiler);
  fastify.setValidatorCompiler(validatorCompiler);
  fastify.patch(
    "/",
    {
      schema: {
        body: updateUserPasswordSchema,
      },
    },
    async (request, reply) => {
      const authedUser = validateAuthUser(request.user);

      const userData = await prisma.users.findUnique({
        where: { id: authedUser.id },
      });
      if (!userData) {
        throw fastify.httpErrors.notFound("ユーザー情報がありません");
      }

      const result = await new Promise((resolve, reject) => {
        bcrypt.compare(request.body.password, userData.password, (err, res) => {
          if (err) {
            reject(fastify.httpErrors.internalServerError());
          } else {
            resolve(res);
          }
        });
      });

      if (!result) {
        throw fastify.httpErrors.badRequest("現在のパスワードが異なります");
      }

      if (request.body.password === request.body.new_password) {
        throw fastify.httpErrors.badRequest(
          "新しいパスワードは現在のパスワードと違うものを入力してください"
        );
      } else if (
        request.body.new_password !== request.body.new_password_confirmation
      ) {
        throw fastify.httpErrors.badRequest("新しいパスワードが一致しません");
      }

      await changePassword({
        user_id: authedUser.id,
        password: request.body.new_password,
      });

      reply.send("OK");
    }
  );
};

export default index;

