struct Node{
    vector<Node*> node;
    int cnt;

    Node(){
        node.resize(26,NULL);
        cnt =0;
    }
};
class Trie {
    public:
    Trie()
    {
        root = new Node();
    }
    void insert(const string& s)
    {
        insert(root,0,s);
    }

    int queryPrefix(const string& s)
    {
        return queryPrefix(root,0,s);
    }

    private:
    Node* root;

    void insert(Node* root,int ind,const string& s)
    {
        if(ind == s.length()) return ;
        int child_ind = s[ind] -'a';
        if(root->node[child_ind]==NULL) root->node[child_ind] = new Node();

        root->node[child_ind]->cnt++;
        insert(root->node[child_ind],ind+1,s);
    }

    int queryPrefix(Node* root,int ind,const string& s)
    {
        if(ind == s.length()) return root->cnt;
        // int child_ind = s[ind] - 'a';
        return (root->cnt + queryPrefix(root->node[s[ind] - 'a'],ind+1,s));
    }
};

class Solution {
public:
    vector<int> sumPrefixScores(vector<string>& words) {
        Trie trie;
        for(string s: words)
        {
            trie.insert(s);
        }
        vector<int> answer;
        for(string s: words)
        {
            answer.push_back(trie.queryPrefix(s));
        }
        return answer;
    }
};

