#define PPM_PIN 37 // Define the GPIO pin where the PPM signal is connected
#define CHANNEL_COUNT 8
#define SYNC_GAP_LENGTH 3000 // Length of the sync pulse gap in microseconds

#define LED_PIN 21
#define DAC_PIN_1 26
#define DAC_PIN_2 25
#define LEFT_REVERSE_PIN 32 // Пин для реверса левого мотора
#define RIGHT_REVERSE_PIN 33 // Пин для реверса правого мотора

#define CH_TURN 3
#define CH_MOVE 1
#define CH_JETSON 4

#define RCCHECK(fn) { rcl_ret_t temp_rc = fn; if((temp_rc != RCL_RET_OK)){error_loop();}}
#define RCSOFTCHECK(fn) { rcl_ret_t temp_rc = fn; if((temp_rc != RCL_RET_OK)){error_loop();}}

volatile uint32_t pulseStartTime = 0;
volatile uint32_t pulseEndTime = 0;
volatile uint32_t pulseWidth = 0;

volatile uint16_t ppmValues[CHANNEL_COUNT];
volatile uint8_t currentChannel = 0;

volatile int left_speed = 0;
volatile int right_speed = 0;
volatile bool left_reverse = false;
volatile bool right_reverse = false;

void error_loop(){
  while(1){
    digitalWrite(LED_PIN, !digitalRead(LED_PIN));
    delay(100);
  }
}

void IRAM_ATTR onPPMPulse() {
    pulseEndTime = micros();
    pulseWidth = pulseEndTime - pulseStartTime;
    pulseStartTime = pulseEndTime;

    if (pulseWidth > SYNC_GAP_LENGTH) {
        currentChannel = 0; // Sync pulse detected, reset channel index
    } else {
        if (currentChannel < CHANNEL_COUNT) {
            ppmValues[currentChannel] = pulseWidth;
            currentChannel++;
        }
    }
}

void subscription_callback(const void *msgin) {
  const geometry_msgs__msg__Twist * msg = (const geometry_msgs__msg__Twist *)msgin;

// Определяем скорость и направление

    float linear = msg->linear.x;
    float angular = msg->angular.z;

    float raw_left_speed = linear - angular;
    float raw_right_speed = linear + angular;

    left_reverse = raw_left_speed < 0;
    right_reverse = raw_right_speed < 0;

    left_speed = (int)(fabs(raw_left_speed) * 130 + 3);
    right_speed = (int)(fabs(raw_right_speed) * 130);

    left_speed = left_speed < 0 ? 0 : left_speed > 255 ? 255 : left_speed;
    right_speed = right_speed < 0 ? 0 : right_speed > 255 ? 255 : right_speed;
}

void setup() {
  set_microros_transports();
  Serial.begin(115200);
  pinMode(PPM_PIN, INPUT);
  pinMode(LED_PIN, OUTPUT);
  pinMode(DAC_PIN_1, OUTPUT);
  pinMode(DAC_PIN_2, OUTPUT);
  pinMode(LEFT_REVERSE_PIN, OUTPUT); // Настраиваем пин реверса левого мотора
  pinMode(RIGHT_REVERSE_PIN, OUTPUT); // Настраиваем пин реверса правого мотора
  attachInterrupt(digitalPinToInterrupt(PPM_PIN), onPPMPulse, RISING);

  delay(2000);
  allocator = rcl_get_default_allocator();

  RCCHECK(rclc_support_init(&support, 0, NULL, &allocator));
  RCCHECK(rclc_node_init_default(&node, NODE_NAME, "", &support));
  RCCHECK(rclc_subscription_init_default(
    &subscriber,
    &node,
    ROSIDL_GET_MSG_TYPE_SUPPORT(geometry_msgs, msg, Twist),
    TOPIC_NAME));

  RCCHECK(rclc_executor_init(&executor, &support.context, 1, &allocator));
  RCCHECK(rclc_executor_add_subscription(&executor, &subscriber, &msg, &subscription_callback, ON_NEW_DATA));      
}

