 */
function findCommonSnippet(str1, str2, length) {
  const minLen = Math.min(str1.length, str2.length);
  for (let i = 0; i <= minLen - length; i++) {
    const snippet = str1.slice(i, i + length);
    if (str2.includes(snippet)) return snippet;
  }
  return null;
