% 目标函数：最小化末端误差和能耗
function f = objective_function(theta)
    global target_pos joint_inertia joint_avg_speed;
    [end_pos, ~] = forward_kinematics(theta);
    f(1) = norm(end_pos - target_pos);  % 末端误差

    mass = 5; g = 9.81;  % 质量和重力加速度
    gravitational_energy = sum(mass * g * forward_kinematics(theta));  % 重力势能

    f(2) = sum(joint_inertia .* (joint_avg_speed .* theta).^2) + gravitational_energy;  % 总能耗
end

% 约束函数：限制末端误差不超过200mm
function [c, ceq] = constraint_function(theta)
    global target_pos;
    [end_pos, ~] = forward_kinematics(theta);
    c = norm(end_pos - target_pos) - 200;
    ceq = [];
end

% 正向运动学计算
function [end_pos, T] = forward_kinematics(theta)
    global DH_params;
    T = eye(4);
    for i = 1:6
        T = T * dh_matrix(DH_params(i,:), theta(i));
    end
    end_pos = T(1:3,4);
end

% DH矩阵计算
function T = dh_matrix(params, theta)
    a = params(1); alpha = deg2rad(params(2)); d = params(3);
    T = [
        cos(theta), -sin(theta)*cos(alpha),  sin(theta)*sin(alpha), a*cos(theta);
        sin(theta),  cos(theta)*cos(alpha), -cos(theta)*sin(alpha), a*sin(theta);
        0,           sin(alpha),             cos(alpha),            d;
        0,           0,                      0,                     1
    ];
end

