// Recursive function to find nth number in the sequence
int customSequence(int n) {
    if (n == 0)
        return 0;
    else if (n == 1 || n == 2)
        return -1;
    else
        return customSequence(n - 1) + customSequence(n - 2) + customSequence(n - 3);
}

